// src/components/organisms/ChartSection.js
import React from "react";
import ApexChart from "react-apexcharts";
import users from "../../data/test_users";

const getStatusColor = (status) => {
  switch (status) {
    case "未登録":
      return "#6B7280"; // gray-500
    case "確認中":
      return "#3B82F6"; // blue-500
    case "承認":
      return "#34D399"; // green-500
    case "拒否":
      return "#EF4444"; // red-500
    case "学生認証":
      return "#8B5CF6"; // purple-500
    default:
      return "#000000"; // black as default
  }
};

const ChartSection = () => {
  // ステータスごとのユーザー数を計算
  const userCounts = users.reduce((counts, user) => {
    counts[user.status] = (counts[user.status] || 0) + 1;
    return counts;
  }, {});

  const chartData = Object.entries(userCounts).map(([status, count]) => ({
    label: status,
    value: count,
    color: getStatusColor(status),
  }));

  const options = {
    labels: chartData.map((item) => item.label),
    colors: chartData.map((item) => item.color),
    legend: {
      show: false,
    },
  };

  const series = chartData.map((item) => item.value);

  return (
    <div className="chart mb-10">
      <ApexChart type="donut" options={options} series={series} width="400" />
    </div>
  );
};

export default ChartSection;
