import React, { useState, useEffect, useMemo } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Header from "../molecules/shared/Header";
import IndustryRow from "../molecules/industries/IndustryRow";
import industriesData from "../../data/industries_test";
import CreateButton from "../atoms/Buttons/CreateButton";
import Pagination from "../organisms/shared/Pagination";

const meta = {
  title: "",
  meta: [],
  link: [],
  style: [],
  script: [],
};

export default function Industries() {
  const [industries, setIndustries] = useState(industriesData);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);
  const [searchQuery, setSearchQuery] = useState("");

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const filteredIndustries = useMemo(() => {
    return industries.filter(
      (industry) =>
        industry.industryName
          .toLowerCase()
          .includes(searchQuery?.toLowerCase() || "") ||
        industry.firstName
          .toLowerCase()
          .includes(searchQuery?.toLowerCase() || "") ||
        industry.lastName
          .toLowerCase()
          .includes(searchQuery?.toLowerCase() || "") ||
        industry.email.toLowerCase().includes(searchQuery?.toLowerCase() || "")
    );
  }, [industries, searchQuery]);

  const totalPages = useMemo(() => {
    return Math.ceil(filteredIndustries.length / itemsPerPage);
  }, [filteredIndustries, itemsPerPage]);

  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;

  const currentIndustries = useMemo(() => {
    return filteredIndustries.slice(startIndex, endIndex);
  }, [filteredIndustries, startIndex, endIndex]);

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
    setCurrentPage(1);
  };

  const memoizedIndustries = useMemo(() => {
    return currentIndustries;
  }, [currentIndustries]);

  return (
    <React.Fragment>
      <HelmetProvider>
        <Helmet {...meta}></Helmet>
      </HelmetProvider>
      <>
        <Header />
        <section className="py-4 overflow-hidden">
          <div className="container px-4 mx-auto">
            <div className="pt-5 pb-7 bg-neutral-50 border border-neutral-100 rounded-xl">
              <div className="px-6">
                <div className="flex flex-wrap items-center justify-between -m-2 mb-5">
                  <div className="w-auto p-2">
                    <h3 className="font-heading text-lg text-neutral-600 font-semibold">
                      業種一覧
                    </h3>
                  </div>
                  <div>
                    <CreateButton link="/industries/new" />
                  </div>
                </div>
                <div className="mb-12 w-full overflow-x-auto">
                  <div className="flex items-center justify-between mb-4">
                    <div className="relative w-full max-w-xs">
                      <input
                        type="text"
                        name="search"
                        id="search"
                        className="block w-full py-2 pl-10 pr-3 leading-5 text-gray-700 placeholder-gray-400 bg-white border border-gray-300 rounded-md shadow-sm transition duration-150 ease-in-out focus:outline-none focus:shadow-outline-blue focus:border-blue-300 sm:text-sm sm:leading-5"
                        placeholder="Search"
                        value={searchQuery}
                        onChange={handleSearch}
                      />
                      <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                        <svg
                          className="w-4 h-4 text-gray-400"
                          viewBox="0 0 16 16"
                          fill="none"
                        >
                          <path
                            d="M6.5 0C2.91015 0 0 2.91015 0 6.5C0 10.0899 2.91015 13 6.5 13C8.23858 13 9.84768 12.3728 11.1315 11.364L15.8536 16.0852C16.0488 16.2803 16.0488 16.5968 15.8536 16.7919C15.6585 16.9871 15.342 16.9871 15.1469 16.7919L10.4248 12.0708C9.3728 12.8315 8.10526 13.25 6.5 13.25C2.91015 13.25 0 10.3399 0 6.75C0 3.16015 2.91015 0.25 6.5 0.25C8.10526 0.25 9.3728 0.668478 10.4248 1.42917L15.1469 4.70809C15.342 4.90322 15.6585 4.90322 15.8536 4.70809C16.0488 4.51296 16.0488 4.19648 15.8536 4.00135L11.1315 0.720339C9.84768 -0.288043 8.23858 -0.915217 6.5 -0.915217C2.91015 -0.915217 0 1.99565 0 6.5C0 11.0043 2.91015 14.9152 6.5 14.9152C11.0043 14.9152 14.9152 11.0043 14.9152 6.5C14.9152 2.99565 11.0043 -0.915217 6.5 -0.915217Z"
                            fill="currentColor"
                          />
                        </svg>
                      </div>
                    </div>
                  </div>
                  <table className="w-full min-w-max">
                    <thead>
                      <tr className="text-left">
                        <th className="p-0 whitespace-nowrap">
                          <div className="pb-3.5">
                            <a className="inline-flex items-center" href="#">
                              <span className="mr-2.5 text-sm text-gray-400 font-medium">
                                業種名
                              </span>
                              <svg
                                width={18}
                                height={18}
                                viewBox="0 0 18 18"
                                fill="none"
                                xmlns="http://www.w3.org/2000/svg"
                              >
                                <path
                                  fillRule="evenodd"
                                  clipRule="evenodd"
                                  d="M4.7636 6.5636C5.11508 6.21213 5.68492 6.21213 6.0364 6.5636L9 9.52721L11.9636 6.5636C12.3151 6.21213 12.8849 6.21213 13.2364 6.5636C13.5879 6.91508 13.5879 7.48492 13.2364 7.8364L9.6364 11.4364C9.28492 11.7879 8.71508 11.7879 8.3636 11.4364L4.7636 7.8364C4.41213 7.48492 4.41213 6.91508 4.7636 6.5636Z"
                                  fill="#7F8995"
                                />
                              </svg>
                            </a>
                          </div>
                        </th>
                        <th className="p-0 whitespace-nowrap">
                          <div className="pb-3.5">
                            <a className="inline-flex items-center" href="#">
                              <span className="mr-2.5 text-sm text-gray-400 font-medium">
                                業種の登録者
                              </span>
                              <svg
                                width={18}
                                height={18}
                                viewBox="0 0 18 18"
                                fill="none"
                                xmlns="http://www.w3.org/2000/svg"
                              >
                                <path
                                  fillRule="evenodd"
                                  clipRule="evenodd"
                                  d="M4.7636 6.5636C5.11508 6.21213 5.68492 6.21213 6.0364 6.5636L9 9.52721L11.9636 6.5636C12.3151 6.21213 12.8849 6.21213 13.2364 6.5636C13.5879 6.91508 13.5879 7.48492 13.2364 7.8364L9.6364 11.4364C9.28492 11.7879 8.71508 11.7879 8.3636 11.4364L4.7636 7.8364C4.41213 7.48492 4.41213 6.91508 4.7636 6.5636Z"
                                  fill="#7F8995"
                                />
                              </svg>
                            </a>
                          </div>
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {memoizedIndustries.map((industry) => (
                        <IndustryRow key={industry.id} industry={industry} />
                      ))}
                    </tbody>
                  </table>
                  <Pagination
                    currentPage={currentPage}
                    totalPages={totalPages}
                    onPageChange={handlePageChange}
                  />
                </div>
              </div>
            </div>
          </div>
        </section>
      </>
    </React.Fragment>
  );
}
