// src/components/organisms/ChartSection.js
import React from "react";
import ApexChart from "react-apexcharts";
import processUserData from "../../helpers/processUserData";
import users from "../../data/test_users";

const chartData = processUserData(users);

const options = {
  labels: chartData.map((item) => item.label),
  legend: {
    show: false,
  },
};

const series = chartData.map((item) => item.value);

const ChartSection = () => (
  <div className="chart mb-10">
    <ApexChart type="donut" options={options} series={series} width="400" />
  </div>
);

export default ChartSection;
