import React, { useState } from "react";
import { User } from "../types";
import DetailsView from "./DetailsView";
import VerificationView from "./VerificationView";

interface Props {
  user?: User;
}

const UserDetails: React.FC<Props> = ({ user }) => {
  const [isVerificationView, setIsVerificationView] = useState(false);

  const handleDetailsClick = () => {
    setIsVerificationView(false);
  };

  const handleVerificationClick = () => {
    setIsVerificationView(true);
  };

  return (
    <React.Fragment>
      <>
        <section className="bg-white border-b py-8">
          <div className="container mx-auto flex flex-wrap pt-4 pb-12">
            <div className="w-full md:w-4/5 mx-auto">
              <div className="flex flex-wrap">
                <div
                  onClick={handleDetailsClick}
                  className={`flex-1 p-1 text-center border ${
                    !isVerificationView
                      ? "bg-blue-500 text-white"
                      : "bg-gray-100 text-neutral-400"
                  }`}
                >
                  <button className="inline-block pb-3 text-sm font-medium">
                    詳細情報
                  </button>
                </div>
                <div
                  onClick={handleVerificationClick}
                  className={`flex-1 p-1 text-center border ${
                    isVerificationView
                      ? "bg-blue-500 text-white"
                      : "bg-gray-100 text-neutral-400"
                  }`}
                >
                  <button className="inline-block pb-3 text-sm font-medium">
                    本人確認
                  </button>
                </div>
              </div>
              {isVerificationView ? (
                <VerificationView />
              ) : (
                <DetailsView user={user ?? null} />
              )}
            </div>
          </div>
        </section>
      </>
    </React.Fragment>
  );
};

export default UserDetails;
