import React, { useState } from "react";
import Modal from "../../organisms/shared/Modal";
import EditModal from "../../organisms/shared/EditModal";

const IndustryRow = ({ industry }) => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isEditModalVisible, setIsEditModalVisible] = useState(false);
  const [editValue, setEditValue] = useState(industry.name);

  const handleDeleteClick = () => {
    setIsModalVisible(true);
  };

  const handleModalCancel = () => {
    setIsModalVisible(false);
  };

  const handleModalConfirm = () => {
    setIsModalVisible(false);
  };

  const handleEditClick = () => {
    setIsEditModalVisible(true);
  };

  const handleEditModalCancel = () => {
    setIsEditModalVisible(false);
  };

  const handleEditModalConfirm = (value) => {
    setEditValue(value);
    setIsEditModalVisible(false);
  };

  return (
    <tr>
      <td className="py-3 pr-4 whitespace-nowrap">
        <div className="flex flex-wrap items-center">
          <span className="font-semibold">{editValue}</span>
        </div>
      </td>
      <td className="py-3 pr-4">
        <span className="font-medium">{industry.registrants}人</span>
      </td>
      <td className="py-3 pr-4">
        <span
          className="font-medium cursor-pointer text-blue-500"
          onClick={handleEditClick}
        >
          編集
        </span>
      </td>
      <td className="flex justify-end py-3 pr-4">
        <span
          className="px-2.5 py-1 text-sm font-medium bg-opacity-10 rounded-full text-red-900 bg-red-900 cursor-pointer"
          onClick={handleDeleteClick}
        >
          削除
        </span>
      </td>
      {isModalVisible && (
        <Modal onCancel={handleModalCancel} onConfirm={handleModalConfirm} />
      )}
      <EditModal
        isVisible={isEditModalVisible}
        onCancel={handleEditModalCancel}
        onConfirm={handleEditModalConfirm}
        initialValue={editValue}
        editTarget="業界名"
      />
    </tr>
  );
};

export default IndustryRow;
