import React, { useState, useEffect } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Header from "../components/organisms/Header";

const meta = {
  title: "",
  meta: [],
  link: [],
  style: [],
  script: [],
};

export default function EventCreate() {
  const [menuVisible, setMenuVisible] = useState(false);

  useEffect(() => {
    const burger = document.querySelector(".navbar-burger");
    const close = document.querySelector(".navbar-close");
    const backdrop = document.querySelector(".navbar-backdrop");

    const toggleMenu = () => {
      setMenuVisible((prevVisible) => !prevVisible);
    };

    if (burger) burger.addEventListener("click", toggleMenu);
    if (close) close.addEventListener("click", toggleMenu);
    if (backdrop) backdrop.addEventListener("click", toggleMenu);

    return () => {
      if (burger) burger.removeEventListener("click", toggleMenu);
      if (close) close.removeEventListener("click", toggleMenu);
      if (backdrop) backdrop.removeEventListener("click", toggleMenu);
    };
  }, []);
  useEffect(() => {
    // Custom CSS classes for elements from the index.html
    const rootElement = document.getElementById("root");
    let classes = rootElement.classList;
    rootElement.classList.remove(...classes);
    // Remove the call to the add method
  });

  return (
    <React.Fragment>
      <HelmetProvider>
        <Helmet {...meta}></Helmet>
      </HelmetProvider>
      <>
        <Header />
        <section className="py-4 overflow-hidden">
          <div className="container px-4 mx-auto">
            <div className="p-6 bg-white border rounded-xl">
              <div className="flex flex-wrap justify-between -m-2">
                <div className="w-full sm:w-1/2 p-2">
                  <h3 className="font-heading text-sm font-semibold">
                    新しいイベントのタイトル
                  </h3>
                </div>
                <div className="w-full sm:w-1/2 p-2">
                  <textarea
                    className="block px-3.5 pb-3.5 pt-2.5 text-sm w-full sm:max-w-md ml-auto placeholder-neutral-400 hover:bg-gray-50 outline-none border border-neutral-200 focus:border-neutral-600 rounded-lg"
                    id="inputsTextarea5-1"
                    rows={4}
                    placeholder="Write a short note"
                    defaultValue={""}
                  />
                </div>
              </div>
              <div className="flex flex-wrap justify-between -m-2">
                <div className="w-full sm:w-1/2 p-2">
                  <h3 className="font-heading text-sm font-semibold">
                    新しいイベントのURL
                  </h3>
                </div>
                <div className="w-full sm:w-1/2 p-2">
                  <textarea
                    className="block px-3.5 pb-3.5 pt-2.5 text-sm w-full sm:max-w-md ml-auto placeholder-neutral-400 hover:bg-gray-50 outline-none border border-neutral-200 focus:border-neutral-600 rounded-lg"
                    id="inputsTextarea5-1"
                    rows={4}
                    placeholder="Write a short note"
                    defaultValue={""}
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="flex justify-center space-x-4">
            {" "}
            <a
              className="inline-flex flex-wrap items-center px-5 py-3.5 text-sm text-neutral-50 font-medium hover:bg-gradient-purple-left-dark rounded-lg transition duration-300 bg-blue-500"
              href="#"
            >
              戻る
            </a>
            <a
              className="inline-flex flex-wrap items-center px-5 py-3.5 text-sm text-neutral-50 font-medium hover:bg-gradient-purple-left-dark rounded-lg transition duration-300 bg-blue-500"
              href="#"
            >
              登録する
            </a>
          </div>{" "}
        </section>
      </>
    </React.Fragment>
  );
}
