import React, { useState } from "react";
import DeleteModal from "../../organisms/shared/DeleteModal";
import EditModal from "../../organisms/shared/EditModal";

interface Industry {
  id: number;
  name: string;
  registeredUsers: number;
}

interface IndustryRowProps {
  industry: Industry;
}

const IndustryRow: React.FC<IndustryRowProps> = ({ industry }) => {
  const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
  const [isEditModalVisible, setIsEditModalVisible] = useState<boolean>(false);
  const [editValue, setEditValue] = useState<string>(industry.name);

  const handleDeleteClick = (): void => {
    setIsModalVisible(true);
  };

  const handleModalCancel = (): void => {
    setIsModalVisible(false);
  };

  const handleModalConfirm = (): void => {
    setIsModalVisible(false);
  };

  const handleEditClick = (): void => {
    setIsEditModalVisible(true);
  };

  const handleEditModalCancel = (): void => {
    setIsEditModalVisible(false);
  };

  const handleEditModalConfirm = (value: string): void => {
    setEditValue(value);
    setIsEditModalVisible(false);
  };

  return (
    <tr className="border-b border-neutral-100">
      <td className="py-3 px-6 text-left whitespace-nowrap">
        <div className="flex items-center">
          <span className="font-medium text-gray-800">{industry.name}</span>
        </div>
      </td>
      <td className="py-3 px-6 text-left whitespace-nowrap">
        <div className="flex items-center">
          <span className="font-medium text-gray-800">
            {industry.registeredUsers}
          </span>
        </div>
      </td>
      <td className="py-3 px-6 text-right whitespace-nowrap">
        <div className="flex items-center justify-end">
          <button
            className="text-neutral-600 hover:text-neutral-900"
            onClick={handleEditClick}
          >
            編集
          </button>
          <button
            className="ml-4 text-red-600 hover:text-red-900"
            onClick={handleDeleteClick}
          >
            削除
          </button>
        </div>
      </td>
      {isModalVisible && (
        <DeleteModal
          isVisible={isModalVisible}
          onCancel={handleModalCancel}
          onConfirm={handleModalConfirm}
          deleteTarget={industry.name}
          initialValue={""}
        />
      )}
      <EditModal
        isVisible={isEditModalVisible}
        onCancel={handleEditModalCancel}
        onConfirm={handleEditModalConfirm}
        editTarget={industry.name}
        initialValue={editValue}
      />
    </tr>
  );
};

export default IndustryRow;
