import { Storage } from "../infrastructure/Storage";
import {
  InvlalidSessionTokenError,
  UnauthorizedError,
  UsersRepository,
} from "../infrastructure/repositories";
import { UserItem } from "../models/presentation/UserItem";

export class FetchUsersInput {}

export class FetchUsersOutput {
  readonly users: UserItem[];

  constructor(params: { users: UserItem[] }) {
    this.users = params.users;
  }
}

export class FetchUsersUsecase {
  fetch(): Promise<FetchUsersOutput> {
    const sessionToken = Storage.restoreSessionToken();
    if (!sessionToken) {
      return Promise.reject(new InvlalidSessionTokenError());
    }

    return new UsersRepository()
      .fetch(sessionToken)
      .then((data) => {
        return new FetchUsersOutput({
          users: data.results.map((user: any) => {
            // <-- これが23行目です
            return new UserItem({
              id: user.id,
              firstName: user.name.split(" ")[0],
              lastName: user.name.split(" ")[1] || "",
              attribute: user.status,
              email: user.email,
            });
          }),
        });
      })
      .catch((e) => {
        if (e instanceof UnauthorizedError) {
          Storage.clear();
        }
        return e;
      });
  }
}
