import React, { useState } from "react";
import Modal from "../../organisms/shared/Modal";

const IndustryRow = ({ industry }) => {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const handleDeleteClick = () => {
    setIsModalVisible(true);
  };

  const handleModalCancel = () => {
    setIsModalVisible(false);
  };

  const handleModalConfirm = () => {
    setIsModalVisible(false);
  };

  return (
    <tr>
      <td className="py-3 pr-4 whitespace-nowrap">
        <div className="flex flex-wrap items-center">
          <span className="font-semibold">{industry.name}</span>
        </div>
      </td>
      <td className="py-3 pr-4">
        <span className="font-medium">{industry.registrants}人</span>
      </td>
      <td className="py-3 pr-4">
        <span className="font-medium">編集</span>
      </td>
      <td className="flex justify-end py-3 pr-4">
        <span
          className="px-2.5 py-1 text-sm font-medium bg-opacity-10 rounded-full text-red-900 bg-red-900 cursor-pointer"
          onClick={handleDeleteClick}
        >
          削除
        </span>
      </td>
      {isModalVisible && (
        <Modal onCancel={handleModalCancel} onConfirm={handleModalConfirm} />
      )}
    </tr>
  );
};

export default IndustryRow;
