import React, { useState } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { ChatRepository } from "../../infrastructure/repositories/ChatRepository";

const meta = {
  title: "",
  meta: [],
  link: [],
  style: [],
  script: [],
};

interface ChatMessage {
  prompt: string;
  response: string;
}

export default function Index() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const [chatHistory, setChatHistory] = useState<ChatMessage[]>([]);
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (loading || prompt.trim() === "") return;

    setLoading(true);
    const chatRepo = new ChatRepository();
    try {
      const data = await chatRepo.post(prompt);
      const newMessage: ChatMessage = { prompt, response: data.response };
      setChatHistory([...chatHistory, newMessage]);
      setResponse(data.response);
      setPrompt("");
    } catch (error) {
      console.error("Error:", error);
      setResponse("Error occurred");
    } finally {
      setLoading(false);
    }
  };

  return (
    <React.Fragment>
      <HelmetProvider>
        <Helmet {...meta}></Helmet>
      </HelmetProvider>
      <div className="flex flex-col h-screen bg-gray-100">
        <div className="flex flex-col flex-grow p-4 overflow-auto">
          {chatHistory.map((chat, index) => (
            <div key={index} className="mb-4 flex">
              {/* User message */}
              <div className="flex items-end ml-auto">
                <div className="bg-blue-500 text-white p-3 rounded-xl rounded-br-none shadow-md max-w-xs">
                  {chat.prompt}
                </div>
                <img
                  src="/path/to/user/icon.png" // ユーザーのアイコン
                  alt="User"
                  className="w-10 h-10 rounded-full ml-2"
                />
              </div>
              {/* Partner message */}
              <div className="flex items-start mt-2">
                <img
                  src="/path/to/partner/icon.png" // 相手のアイコン
                  alt="Partner"
                  className="w-10 h-10 rounded-full mr-2"
                />
                <div className="bg-gray-300 text-black p-3 rounded-xl rounded-bl-none shadow-md max-w-xs">
                  {chat.response}
                </div>
              </div>
            </div>
          ))}
          {loading && (
            <div className="flex justify-center items-center">
              <div className="loader border-t-4 border-blue-500 rounded-full w-8 h-8 animate-spin"></div>
            </div>
          )}
        </div>
        <div className="p-4 bg-white flex items-center border-t border-gray-200">
          <textarea
            className="flex-grow p-2 border rounded-lg mr-2"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your prompt"
          />
          <button
            className={`bg-blue-500 text-white p-2 rounded-lg ${loading ? "opacity-50 cursor-not-allowed" : ""}`}
            onClick={handleSend}
            disabled={loading}
          >
            送信
          </button>
        </div>
      </div>
    </React.Fragment>
  );
}
