import React, { useEffect, useState } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { ChatRepository } from "../../infrastructure/repositories/ChatRepository";

const meta = {
  title: "",
  meta: [],
  link: [],
  style: [],
  script: [],
};

export default function Index() {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const handleSend = async () => {
    const chatRepo = new ChatRepository();
    try {
      const data = await chatRepo.post(prompt);
      setChatHistory([...chatHistory, { prompt, response: data.response }]);
      setResponse(data.response);
      setPrompt("");
    } catch (error) {
      console.error("Error:", error);
      setResponse("Error occurred");
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      const chatRepo = new ChatRepository();
      await chatRepo.get(); // ここに適切なデータを渡す
    };
    fetchData();
  }, []);

  return (
    <React.Fragment>
      <HelmetProvider>
        <Helmet {...meta}></Helmet>
      </HelmetProvider>
      <div className="flex flex-col h-screen bg-gray-100">
        <div className="flex flex-col flex-grow p-4 overflow-auto">
          {chatHistory.map((chat, index) => (
            <div key={index} className="mb-4">
              <div className="bg-blue-500 text-white p-3 rounded-lg shadow-md w-fit self-start">
                {chat.prompt}
              </div>
              <div className="bg-gray-300 text-black p-3 rounded-lg shadow-md w-fit self-end mt-2">
                {chat.response}
              </div>
            </div>
          ))}
        </div>
        <div className="p-4 bg-white flex items-center border-t border-gray-200">
          <textarea
            className="flex-grow p-2 border rounded-lg mr-2"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Enter your prompt"
          />
          <button
            className="bg-blue-500 text-white p-2 rounded-lg"
            onClick={handleSend}
          >
            Send
          </button>
        </div>
      </div>
    </React.Fragment>
  );
}
